import type { NumberLike } from "@nomicfoundation/hardhat-network-helpers/src/types";

import { getHardhatProvider, toRpcQuantity } from "@nomicfoundation/hardhat-network-helpers/src/utils";

import { millis } from "@nomicfoundation/hardhat-network-helpers/src/helpers/time/duration";

/**
 * Sets the timestamp of the next block but doesn't mine one.
 *
 * @param timestamp Can be `Date` or Epoch seconds. Must be greater than the latest block's timestamp
 */
export async function setNextBlockTimestamp(
  timestamp: NumberLike | Date
): Promise<void> {
  const provider = await getHardhatProvider();

  const timestampRpc = toRpcQuantity(
    timestamp instanceof Date ? millis(timestamp.valueOf()) : timestamp
  );

  await provider.request({
    method: "evm_setNextBlockTimestamp",
    params: [timestampRpc],
  });
}
