import { toUtf8Bytes, toUtf8String } from "ethers/src.ts/utils/utf8.js";

import { Typed } from "ethers/src.ts/abi/typed.js";
import { DynamicBytesCoder } from "ethers/src.ts/abi/coders/bytes.js";

import type { Reader, Writer } from "ethers/src.ts/abi/coders/abstract-coder.js";


/**
 *  @_ignore
 */
export class StringCoder extends DynamicBytesCoder {

    constructor(localName: string) {
        super("string", localName);
    }

    defaultValue(): string {
        return "";
    }

    encode(writer: Writer, _value: string | Typed): number {
        return super.encode(writer, toUtf8Bytes(Typed.dereference(_value, "string")));
    }

    decode(reader: Reader): any {
        return toUtf8String(super.decode(reader));
    }
}
