/**
 *  About Cloudflare
 *
 *  @_subsection: api/providers/thirdparty:Cloudflare  [providers-cloudflare]
 */

import { assertArgument } from "ethers/src.ts/utils/index.js";

import { Network } from "ethers/src.ts/providers/network.js";
import { JsonRpcProvider } from "ethers/src.ts/providers/provider-jsonrpc.js";

import type { Networkish } from "ethers/src.ts/providers/network.js";

/**
 *  About Cloudflare...
 */
export class CloudflareProvider extends JsonRpcProvider {
    constructor(_network?: Networkish) {
        if (_network == null) { _network = "mainnet"; }
        const network = Network.from(_network);
        assertArgument(network.name === "mainnet", "unsupported network", "network", _network);
        super("https:/\/cloudflare-eth.com/", network, { staticNetwork: network });
    }
}
